import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import MediaControlCard from "../components/MediaControlCard";
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import profile from "../assets/NewJeans.jpg";
import styled from 'styled-components';
import TextField from "@mui/material/TextField";
import Box from '@mui/material/Box';
import Grid from '@mui/material/Unstable_Grid2';
import { useState } from "react";
import { useRef } from "react";
import { userState, memberState } from "../recoil/atom";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Avvvatars from 'avvvatars-react';
import { Avatar} from "antd";
import EditIcon from '@mui/icons-material/Edit';
import { useRecoilValue, useRecoilState } from "recoil";
import axios from "axios";

const Profile = styled.img`
    width: 300px;
    height: 300px;
`;

export default function MediaCard() {

    const user = useRecoilValue(userState);
    const [member, setMember] = useRecoilState(memberState);
    const memberUser = JSON.parse(localStorage.getItem("user"));

    useEffect(() => {
      axios.get(`/api/member/my/${memberUser.id}`).then((response) => {
        setMember(response.data);
        console.log(member);
      });
    }, []);

    const navigate = useNavigate();

    const [Image, setImage] = useState("https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png")
    const fileInput = useRef(null)
    const [File, setFile] = useState("")

    const onChange = (e) => {
    	if(e.target.files[0]){
                setFile(e.target.files[0])
            }else{ //업로드 취소할 시
                setImage("https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png")
                return
            }
    	//화면에 프로필 사진 표시
            const reader = new FileReader();
            reader.onload = () => {
                if(reader.readyState === 2){
                    setImage(reader.result)
                }
            }
            reader.readAsDataURL(e.target.files[0])
        };

    return (
      <Box sx={{flexGrow : 1 }}>
      <Grid container spacing = {2}>
        <Grid xs = {6}>
        <Card sx={{ maxWidth: 1500 }, {flexDirection : 'row'}}>
          <Profile
            src={profile}>
          </Profile>
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              <Avvvatars value = {member.name}
                size={25} shadow={true}
                radius={10} border={false}
                borderSize = {8} borderColor = "lightblue" />
              <br />
              <Typography
                value = {member.email} />
              <Typography
                value = {member.phone} />
              <br />
              <Typography
                value = {member.pwd} />
              <Typography
                value = {member.team_name} />
            </Typography>
            <Typography variant="body2" color="text.secondary">
            </Typography>
          </CardContent>
          <CardActions>
            <Button
            size="medium"   style={{position: 'absolute', right: 0, marginRight: 5, marginBottom : 30 }}
            onClick = { () => navigate("/profile/modify")}>
            <EditIcon/>
            </Button>
          </CardActions>
        </Card>
        </Grid>
        <Grid xs = {6}>
              <Avatar
                      src={Image}
                      style={{margin:'20px'}}
                      size={200}
                      onClick={()=>{fileInput.current.click()}}/>
               <input
               	type='file'
                  	style={{display:'none'}}
                      accept='image/jpg,impge/png,image/jpeg'
                      name='profile_img'
                      onChange={onChange}
                      ref={fileInput}/>
              <br />
              <MediaControlCard />
            </Grid>
         </Grid>
        </Box>
      );
}
